<script>
    import userBusiness from '../../assets/js/business/user/userBusiness';
    import vueSeamless from 'vue-seamless-scroll'
    import redPack from '../commots/redPack.vue';
    import lotteryApi from  "../../assets/js/api/lotteryApi";
import messageBox from '../../controls/messageBox';
    export default {
        data () {
            return {
                visible:false,
                year:'',
                month:'',
                day:'',
                weeked:'',
                listData: [],
                sign:'',
                signDays:0,
                user:{}
                
            }
        },
        created(){
            this.initUser();
            this.findDividendAndSignRecord();
            this.findSignActivity();
            this.getTodayDate();
        },
        computed: {
              classOption: function () {
                return {
                  step: 0.5,
                  limitMoveNum: 13
                }
              }
        },
        methods: {
            goToVip(){
                this.$router.push("/vip");
            },
            findSignActivity(){
                lotteryApi.signActivity(null,res=>{
                   if(res.data){
                       this.sign = res.data.sign;
                       this.signDays = res.data.days;
                   }
                });
            },
            initUser(){
            const _this = this;
            userBusiness.getUser(this,function (user) {
                _this.user=user;
            });
            },
            signActivity(){
                const _this =this;
                lotteryApi.signActivity({type:1},res=>{
                    if(_this.user.init == false){
                        _this.lalterError('您还没有登录，不能签到，请先登录谢谢！');
                        return
                    }
                    if(res.msg=='签到成功!'){
                        _this.findSignActivity();
                    }
                     messageBox.alterWarning(res.msg)
                });
            },
            findDividendAndSignRecord(){
                lotteryApi.findDividendAndSignRecord(null,res=>{
                    this.listData = res.data 
                });
            },
            getTodayDate(){
               var xingqi
                var mydate=new Date(); 
                this.year=mydate.getFullYear(); 
                this.month=mydate.getMonth()+1;
                this.day=mydate.getDate();
                var myday=mydate.getDay();
                switch(myday) 
                { 
                case 0:xingqi="星期日";break; 
                case 1:xingqi="星期一";break; 
                case 2:xingqi="星期二";break; 
                case 3:xingqi="星期三";break; 
                case 4:xingqi="星期四";break; 
                case 5:xingqi="星期五";break; 
                case 6:xingqi="星期六";break; 
                default:xingqi="系统错误！" 
                }
                this.weeked = xingqi;
            },
            toActivity(){
                userBusiness.toActivity();
            },
            toSetting(){
                if(userBusiness.checkIsNotLoginPlayer()){
                    return;
                }

                this.$router.push("/setting")
            },
            toMyAccount(){
              if(userBusiness.checkIsNotLoginPlayer()){
                  return;
              }

              this.$router.push("/account")
            },
            onlineServer() {
                userBusiness.openCustomerService();
            },
            toRedPack(){
                this.visible = !this.visible;
            },
            closeModal(){
                this.visible = !this.visible;
            },
            myRedPack(){
               this.$refs.page1.showHb(true,true);
            }
        },
         components: {
            vueSeamless,
           'v-redPack': redPack,
        }
    }
</script>

