.polaroid{
   animation: swing 2s ease-in-out forwards infinite; 
   transform-origin: 50% -10em;
}
@keyframes swing {
    0% {
    transform:  rotate(1deg);
    }
    50% {
        transform: rotate(-1deg);
    }
    100% {
        transform:  rotate(1deg);
    }
}
@keyframes bounceInUp-data-lfet {
    0% {
		opacity: 0;
		transform: translateX(-500px)
	}
	60% {
		opacity: 0.7;
		transform: translateX(15px)
	}

	80% {
        opacity: 0.9;
		transform: translateX(-10px)
	}

	100% {
        opacity: 1;
		transform: translateX(0)
	}

}
@keyframes bounceInUp-data-right {
	0% {
		opacity: 0;
		transform: translateX(500px)
	}

	60% {
		opacity: 0.7;
		transform: translateX(-15px)
	}

	80% {
        opacity: 0.9;
		transform: translateX(10px)
	}

	100% {
        opacity: 1;
		transform: translateX(0)
	}
}
@keyframes bounceInUp-data-top {
	0% {
		opacity: 0;
		transform: translateY(500px)
	}

	60% {
		opacity: 0.7;
		transform: translateY(-5px)
	}

	80% {
        opacity: 0.9;
		transform: translateY(10px)
	}

	100% {
        opacity: 1;
		transform: translateY(0)
	}
}
